import React, { useEffect, useState, useContext } from 'react';
import './TopBar.scss';
import { AppContext } from "../../../context/AppContext";
import withRouter from "next/dist/client/with-router";
import FA from "react-fontawesome";

const TopBar = props => {
    const contextData = useContext(AppContext);

    const AdminSideBarOpenCloseHandler=()=>{
        contextData.settings.adminPanelSideBar ?
            contextData.dispatchSettings(settings=>({
                ...settings,
                adminPanelSideBar:false,
            })):
            contextData.dispatchSettings(settings=>({
                ...settings,
                adminPanelSideBar:true,
            }))
    };

    const goToHomePage = ()=>{
        props.router.push('/')
        console.log( props.router)
    };

    const newAction = ()=>{
        console.log( 'there is no action defined')
    };

    

    return (
        <div className='adminTopBar'>
            <div className="adminTopBarControl">
                <button className='adminSideBarMobileBtn' onClick={()=>AdminSideBarOpenCloseHandler()}> <FA className='fontawesomeMedium' name="bars" /> </button>
                <button className='adminSideBarMobileBtn' onClick={()=>goToHomePage()}> <FA className='fontawesomeMedium' name="home" /> </button>
                <button className='adminSideBarMobileBtn' onClick={()=>newAction()}> <FA className='fontawesomeMedium' name="plus" /> </button>
            </div>
            <button className='adminSideBarMobileBtn' onClick={()=>AdminSideBarOpenCloseHandler()}> <FA className='fontawesomeMedium' name="user" /> </button>
        </div>
    );
};
export default withRouter(TopBar);
