                {/*<Widget component={Videos} title='latest video' mainLinkUrl='/posts/' redirectToTitle='More videos' pagination={true} contextData={contextData} />*/ }
                <Posts />
            </div>
        </AppLayout>
    );
};

Home.getInitialProps = async ({pathname,query}) => {
    let data = {
