        size: 30,
        totalPosts: 0,
        postType: 'all',
        keyword: '',
        status: 'all',
        author: 'all',
        fields: [ 'title', 'mainThumbnail', 'quality', 'likes', 'disLikes', 'views', 'duration' ],
        checkedPosts: [],
    };
     // const posts = await getPosts(data);
    // let testData = {
    //     name: 'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx',
    //     pathname,
    //     query
    // };
    // console.log(testData )

    return { }
};
export default withRouter(Home);
