import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../../components/layouts/AdminLayout'
import { AppContext } from '../../../../context/AppContext'
import ColorSection from '../../../../components/adminIncludes/design/ColorSection'

const MyComponent = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        headerBackgroundColor:  '',
        headerTextColor:  '',
        headerBackgroundImageURl:''
    });
    useEffect(() => {
        setState({
            ...state,
            headerBackgroundColor: contextData.siteDesign.headerBackgroundColor,
            headerTextColor: contextData.siteDesign.headerTextColor,
            headerBackgroundImageURl : contextData.siteDesign.topBarTextColor || ''
        })
    }, []);

    return (
        <AdminLayout>
            <div className='header-setting'>
                <div className='colorSettingSections'>
                    <ColorSection designName='headerBackgroundColor'/>
                    <ColorSection designName='headerTextColor'/>
                </div>
            </div>
        </AdminLayout>
    );
};
export default MyComponent;
