import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout';
import {fileUpload} from '../../../_variables/ajaxVariables'

const gallery = props => {
    const uploadInputElement = useRef(null)
    const [ state, setState ] = useState({});
    useEffect(() => {
    }, []);

    const onUploadHandler = e => {
        let file = new FormData()
        file.append('file', e.target.files[0]);
        file.append('token',localStorage.wt)
        fileUpload(file,'test')
    }

    return (
        <AdminLayout>
            <div className='gallery'>
                <div className='uploadControl'>
                    <input ref={ uploadInputElement } type="file" style={ { display: 'none' } } onChange={()=>onUploadHandler()}/>
                    <button onClick={ () => uploadInputElement.current.click() }>Upload</button>
                </div>

            </div>
        </AdminLayout>
    );
};
export default gallery;
