import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../../components/layouts/AdminLayout';
import { AppContext } from '../../../../context/AppContext'
import { DelayInput } from 'react-delay-input'
import './topBar.scss'

const topBar = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        topBarBackgroundColor: '',
        topBarTextColor: ''
    });
    useEffect(() => {
        setState({
            ...state,
            topBarBackgroundColor: contextData.siteDesign.topBarBackgroundColor,
            topBarTextColor: contextData.siteDesign.topBarTextColor
        })
    }, []);

    const onChangeHandler = e => {
        contextData.dispatchSiteDesign({
            ...contextData.siteDesign,
            customScript: e.target.value
        })
    }

    return (
        <AdminLayout>
            <div className='topBarSettings'>
                <div className="topBarSettingsSection colorSettingSection">
                    <p>Top Bar Background Color :</p>
                    <DelayInput className='topBarSettingsSectionInput' name='topBarBackgroundColor' value={ contextData.siteDesign.topBarBackgroundColor } delayTimeout={ 1000 } onChange={ e => onChangeHandler(e) }/>
                    <div className="previewColor" style={ { backgroundColor: contextData.siteDesign.topBarBackgroundColor } }/>
                </div>
            </div>
        </AdminLayout>
    );
};
export default topBar;
