import React, { useEffect, useState, useContext } from 'react';
import { AppContext } from "../../../../../context/AppContext";

const TextInputWithUploadBtn = props => {
    const contextData = useContext(AppContext);
    return (
        <div className='TextInputWithUploadBtn VideoInformationSection'>
            <div className="title">
                <p>{ props.name }</p>
            </div>
            <div className="editor">
                <input className='textInputWithUpload' name={ props.name } onBlur={ e => {
                    props.onChangeHandler(e)
                    e.target.value = contextData.editingPostData[e.target.name]
                } }/>
                <button className='uploadBtn'>Upload</button>
            </div>
        </div>
    );
};
export default TextInputWithUploadBtn;
