import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout';
import {fileUpload} from '../../../_variables/ajaxVariables'


const gallery = props => {
    const uploadInputElement = useRef(null)
    const [selectedFiles,setSelectedFiles]=useState(null)



    const onUploadHandler =  e => {
        const filesData = new FormData()
        filesData.append('uploadingFile',e.target.files[0])
        fileUpload(filesData,'test')
    }
    
    return (
        <AdminLayout>
            <div className='gallery'>
                <div className='uploadControl'>
                    <input ref={ uploadInputElement } type="file" style={ { display: 'none' } } onChange={e=>onUploadHandler(e)}/>
                    <button onClick={ () => uploadInputElement.current.click() }>Add</button>

                </div>
                {/*<form*/}
                {/*      // ref='uploadForm'*/}
                {/*      // id='uploadForm'*/}
                {/*      action={'/api/v1/settings/fileManagerControllers-uploadFile'}*/}
                {/*      method='post'*/}
                {/*      encType="multipart/form-data"*/}
                {/*      onSubmit={e=>e.preventDefault()}*/}
                {/*>*/}
                {/*     <input type="file" name="sampleFile" />*/}
                {/*     <input type='submit' value='Upload!' />*/}
                {/*</form>*/}

            </div>
        </AdminLayout>
    );
};
export default gallery;
