import React,{useEffect,useState,useContext} from 'react';
import Link from "next/link";
const TagsAndCategories = props => {
    const [state, setState] = useState({
        data:[],
        type:'tags'
    });
    useEffect(()=>{
        if (props.data){
            setState({
                data: props.data
            })
        }
        if (props.type){
            setState({
                type: props.type
            })
        }

    },[]);

    const renderData = state.data.map(item=>{
        const path = '/' + state.type + '/' + item;
        return(
            <Link href={path} key={item}><a>{item}</a></Link>
        )
    });

    if (props.data){
        return (
            <div className={state.type + ' tags-categories'}>
                {renderData}
            </div>
        );
    }else return null

};
export default TagsAndCategories;
