import React, { useEffect, useState, useContext } from 'react';

const TextInputWithUploadBtn = props => {
    const [ state, setState ] = useState({
        text: ''
    });

    useEffect(() => {

    }, []);

    const onWriteHandler = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    };

    return (
        <div className='TextInputWithUploadBtn VideoInformationSection'>
            <div className="title">
                <p>{ props.name }</p>
            </div>
            <div className="editor">
                <input className='textInputWithUpload' name={ props.name } onBlur={ e => props.onChangeHandler(e) }/>
                <button className='uploadBtn'>Upload</button>
            </div>
        </div>
    );
};
export default TextInputWithUploadBtn;
