import React, { useEffect, useRef, useState } from 'react';
import jwtDecode from "jwt-decode";
import jwt from 'jsonwebtoken';
import axios from "axios";
import { withRouter } from "next/router";

export const AppContext = React.createContext();

const AppProvider = props => {

    const [ state, dispatchState ] = useState({
        loading:false
    });
    const [ settings, dispatchSettings ] = useState({
        adminPanelSideBar: false,
        test: false
    });
    const [ userData, dispatchUserData ] = useState({});
    const [ editingPostData, dispatchEditingPostData ] = useState({});
    const [ adminPosts, dispatchAdminPosts ] = useState([]);
    const [ adminPostsData, dispatchAdminPostsData ] = useState({
        pageNo: 2,
        size: 30,
        totalPosts: 0,
        postType: 'all',
        keyword:'',
        status:'all',
        author:'all',
        fields:[ 'author', 'title', 'mainThumbnail', 'status', 'actors', 'tags', 'categories' ],
        checkedPosts: [],
    });
    const [ functions, dispatchFunctions ] = useState({
        getAndSetUserInfo: async () => {
            if (localStorage.wt) {
                await axios.post('/api/v1/users/getUserInfo', { token: localStorage.wt }).then(res => {
                    dispatchUserData({ ...userData, ...res.data.userData });
                }).catch(err => {
                    console.log(err);
                })
            }
        },
        logOutUser: () => {
            localStorage.removeItem('wt');
            dispatchUserData({})
            props.router.push('/')
        },
        goToAdminPanel: () => {
            props.router.push('/admin')
        },
        goToHomePage: () => {
            // props.router.push('/')
        },
        savePosts: async (data) => {
            const body = {
                postData: data,
                token: localStorage.wt
            };
            return axios.post('/api/v1/posts/createNewPost', body)
        },
        getPosts: async (data) => {
            const body = {
                ...data,
                token: localStorage.wt
            };
             await axios.post('/api/v1/posts', body).then(res=>{
                 dispatchAdminPosts(res.data.posts);
                 dispatchAdminPostsData({
                     ...adminPostsData,
                     totalPosts:  parseInt(res.data.totalCount),
                 })
             })
        }, setEditingPostData: (name, value) => {
            dispatchEditingPostData(editingPostData => ({
                ...editingPostData,
                [name]: value
            }))
        }, bulkActionPost: (ids,status) => {
            dispatchState({
                ...state,
                loading: true
            });
            const body = {
                ids,
                status,
                token: localStorage.wt
            };
             axios.post('/api/v1/posts/postsBulkAction', body).then(()=>{
                 dispatchState({
                     ...state,
                     loading: false
                 });

             }).catch(()=>{
                 dispatchState({
                     ...state,
                     loading: false
                 });
             })
        }, deletePost: (id) => {
            const body = {
                _id:id,
                token: localStorage.wt
            };
            return axios.post('/api/v1/posts/deletePost', body)
        }

    });

    useEffect(() => {
        functions.getAndSetUserInfo()
    }, []);

    useEffect(() => {
        console.log(adminPostsData)
        console.log(Math.floor(1000/30))

    }, [ adminPostsData ]);

    useEffect(() => {
        console.log(props)
        if (props.router.pathname === '/admin/posts') {
            functions.getPosts(adminPostsData)

        }
    }, [ adminPostsData.pageNo,adminPostsData.size,adminPostsData.postType,adminPostsData.keyword,adminPostsData.status,adminPostsData.fields ]);



    return (
        <div>
            <AppContext.Provider
                value={ {
                    state,
                    dispatchState,
                    settings,
                    dispatchSettings,
                    userData,
                    dispatchUserData,
                    functions,
                    editingPostData,
                    dispatchEditingPostData,
                    adminPosts,
                    dispatchAdminPosts,
                    adminPostsData,
                    dispatchAdminPostsData,
                } }>

                { props.children }
            </AppContext.Provider>
        </div>
    )
};

AppProvider.getInitialProps = (ctx) => {
    return { ctx }
};

export const AppProviderWithRouter = withRouter(AppProvider);
