import React, { useEffect, useRef, useState } from 'react';
import jwtDecode from "jwt-decode";
import jwt from 'jsonwebtoken';
import axios from "axios";

export const AppContext = React.createContext();

const AppProvider = props => {

    const [ state, dispatchState ] = useState({});
    const [ settings, dispatchSettings ] = useState({
        adminPanelSideBar: false,
        test: false
    });
    const [ userData, dispatchUserData ] = useState({
        username: 'Guest'
    });
    const [ functions, dispatchFunctions ] = useState({
        getAndSetUserInfo: async ()=>{
            return await axios.post('/api/v1/users/getUserInfo', { token: localStorage.wt })
        }
    });

    useEffect(()=>{
        if (localStorage.wt){
            useEffect(() => {
                if (localStorage.wt) {
                    functions.getUserInfo().then(res => {
                        functions.setUserInfo(res)
                        state.functions.requestForGoingOnline();
                        props.history.push('/chat')
                    }).catch(() => {
                        localStorage.removeItem('wt');
                        if (props.location.pathname === '/chat' ){
                            props.history.push('/')
                        }
                    })
                }else {
                    if (props.location.pathname === '/chat' ){
                        props.history.push('/')
                    }
                }
            }, [props.location.pathname]);
        }
    },[ ]);






    return (
        <div>
            <AppContext.Provider
                value={ {
                    state,
                    dispatchState,
                    settings,
                    dispatchSettings,
                    userData,
                    dispatchUserData,
                    functions
                } }>

                { props.children }
            </AppContext.Provider>
        </div>
    )
}

export const AppProviderWithRouter = AppProvider;
