import React, {useEffect, useState, useContext, useRef} from 'react';
import {AppContext} from "../../../../../../context/AppContext";
import {getPosts, postsBulkAction,emptyTrash} from "../../../../../variables/_ajaxPostsVariable";

const FilterDropDownActions = props => {
    const bulkAction = useRef(null);
    const typeToDisplay = useRef(null);
    let contextData = useContext(AppContext);
    const [state, setState] = useState({});
    useEffect(() => {
        typeToDisplay.current.value = contextData.postsData.type
    }, []);

    useEffect(()=>{
        console.log( contextData.postsData.checkedPosts)
    },[ contextData.postsData.checkedPosts]);

    const setData = ()=>{
        getPosts(
            contextData.postsData.type, contextData.postsData.size, contextData.postsData.pageNo,
            ['author', 'title', 'imageUrl', 'status', 'actors', 'tags', 'categories'], contextData.postsData.status,
            contextData.postsData.author, contextData.postsData.keyword).then(res => {
            contextData.setPostsData({
                ...contextData.postsData,
                posts: res.data.posts,
                error: res.data.error,
                totalCount: res.data.totalCount
            });
            contextData.setState({
                ...contextData.state,
                loading: false
            });
        }).catch(() => {
            contextData.setState({
                ...contextData.state,
                login: false
            })
        })
    }
    const onFormatChangeHandler = () => {
        if (bulkAction.current.value !== 'none') {
            contextData.setState({
                ...contextData.state,
                loading:true
            });
            postsBulkAction(contextData.postsData.checkedPosts,bulkAction.current.value).then(()=>{
               setData()
            }).catch(err=>{
                contextData.setState({
                    ...contextData.state,
                    loading:false
                })
            })

            // contextData.setPostsData({
            //     ...contextData.postsData,
            //     statusActionForCheckedPosts: bulkAction.current.value
            // })
        }
    };
    // if (contextData.postsData.statusActionForCheckedPosts !== 'none'){
    //     updateManyPostStatus(contextData.postsData.checkedPosts,contextData.postsData.statusActionForCheckedPosts)
    // }

    const onFormatChangeSubmitHandler=()=>{

    }

    const changePostsTypeToDisplay = e=>{
        if (e.target.value !== 'none') {
            contextData.setPostsData({
                ...contextData.postsData,
                type: e.target.value
            })
        }
    };


    const onEmptyTrashHandler = ()=>{
        emptyTrash().then(res=>{
            if (res.data.error){
                contextData.setState({
                    ...contextData.state,
                    report:res.data.message,
                    reportColor:'red'
                })
            }else {
                contextData.setState({
                    ...contextData.state,
                    loading: false
                });
                setData();
                contextData.setState({
                    ...contextData.state,
                    report:res.data.message,
                    reportColor:'green'
                })
            }
        }).catch(()=>{
            contextData.setState({
                ...contextData.state,
                report:'Can Not Communicate With Server'
            })
        })
    }


    const EmptyTrash =()=>{
        if (contextData.postsData.status === 'Trash'){
            return (
                <button onClick={()=>onEmptyTrashHandler()}>Empty Trash</button>
            )
        }else return null
    };

    return (
        <div className='FilterDropDownActions'>
            <div className="bulkAction">
                <select ref={bulkAction} placeholder='Bulk Actions' >
                    <option value='none'>Bulk Actions</option>
                    <option value='Published'>Published</option>
                    <option value='Draft'>Draft</option>
                    <option value='Trash'>Trash</option>
                </select>
                <button className='actionBtn' onClick={() => onFormatChangeHandler()}>Apply</button>
            </div>
            <div className="DateCategoryFotmat">
                {/*<select>*/}
                {/*    <option>All dates</option>*/}
                {/*</select>*/}
                {/*<select>*/}
                {/*    <option>All Posts Categories</option>*/}
                {/*</select>*/}
                <select ref={typeToDisplay} onChange={e => changePostsTypeToDisplay(e)}>
                    {/*<option value='none'>All formats</option>*/}
                    <option value='Video'>Video</option>
                    <option value='Standard'>Standard</option>
                </select>
            </div>
            <EmptyTrash/>
        </div>
    );
};
export default FilterDropDownActions;
