            value
        }
        // props.onChangeHandler(target)
    }



    return (
        <div className='IsInSlideShow VideoInformationSection'>
            <div className="title">
                <p>Slide Show</p>
            </div>
            <div className="editor ">
                <select name='inSlideShow' onChange={ e =>props.onChangeHandler(e)} defaultValue={props.postData.inSlideShow}>
                    <option value='true'>Yes</option>
                    <option value='false'>No</option>
                </select>
                <Switch name='inSlideShow' onChange={ e =>onChangeHandler(e)} checked={ onChangeHandler(props.postData.inSlideShow) }/>
            </div>
        </div>
    );
};
export default IsInSlideShow;
