import React, { useEffect, useState, useContext, useRef } from 'react';
import { AppContext } from "../../../../context/AppContext";

const PostCategoriesTagsActors = props => {
    const contextData = useContext(AppContext);
    const [ items, setItems ] = useState([]);
    let newItemsElement = useRef(null);
    useEffect(() => {
        if (props.isNewPost) {

        }
        console.log(props, props.type)
    }, []);

    const deleteItem = (e) => {
        // contextData.setPostData({
        //     ...contextData.postData,
        //     post: {
        //         ...contextData.postData.post,
        //         tags: contextData.postData.post.tags.filter(tag => {
        //             return tag != e.target.name
        //         })
        //     }
        // })
    };
    // useEffect(()=>{
    //     if (contextData.editingPostData.format === 'video'){
    //         contextData.dispatchEditingPostData(editingPostData=>({
    //             ...editingPostData,
    //             tags:[],
    //             categories:[],
    //             actors:[]
    //         }))
    //     }else {
    //         contextData.dispatchEditingPostData(editingPostData=>({
    //             ...editingPostData,
    //             tags:[],
    //             categories:[]
    //         }))
    //     }
    // },[ contextData.editingPostData.format]);

    const addNewItem = () => {

        if (newItemsElement.current.value.includes(',')) {
            let newItems = newItemsElement.current.value.split(',');
            if (contextData.editingPostData[props.type]) {
                contextData.dispatchEditingPostData(editingPostData => ({
                    ...editingPostData,
                    [props.type]: [...contextData.editingPostData[props.type],...newItems]
                }))
            }else {
                contextData.dispatchEditingPostData(editingPostData => ({
                    ...editingPostData,
                    [props.type]: newItems
                }))
            }
        } else {
            let newItem = newItemsElement.current.value;
            if (contextData.editingPostData[props.type]) {
                contextData.dispatchEditingPostData(editingPostData => ({
                    ...editingPostData,
                    [props.type]: [...contextData.editingPostData[props.type],...newItems]
                }))
            }else {
                contextData.dispatchEditingPostData(editingPostData => ({
                    ...editingPostData,
                    [props.type]: newItems
                }))
            }
        }
    };

    let renderTags = items.map(item => {
        return (
            <div key={ item } className='tagElement'>
                <p>{ item }</p>
                <button name={ item } onClick={ (e) => deleteItem(e) } className='fas fa-times-circle'/>
            </div>
        )
    });

    return (
        <div className='PostCategoriesTagsActors'>
            <div className="addNewTag">
                <input ref={ newItemsElement } type='text'/>
                <button onClick={ () => addNewItem() }> Add</button>
            </div>
            <span>Separate tags with commas</span>
            <div className="tags">
                { renderTags }
            </div>
        </div>
    );
};
export default PostCategoriesTagsActors;
