import React, { useEffect, useState, useContext } from 'react';
import Switch from "react-switch";
import { Checkbox } from 'pretty-checkbox-react';
import './IsInSlideShow.scss';


const IsInSlideShow = props => {
    const [ state, setState ] = useState({
        isChecked: false
    })

    useEffect(() => {
        setState({
            ...state,
            isChecked:  props.postData.inSlideShow || false
        })
    }, [props.postData.inSlideShow]);
    return (
        <div className='IsInSlideShow VideoInformationSection'>
            <div className="title">
                <p>Slide Show</p>
            </div>
            <div className="editor ">
                <select name='inSlideShow' onChange={ e =>props.onChangeHandler(e)} defaultValue={props.postData.inSlideShow}>
                    <option value='true'>Yes</option>
                    <option value='false'>No</option>
                </select>
                <Switch name='inSlideShow' onChange={ e =>props.onChangeHandler(e)} checked={ state.isChecked }/>
                <Checkbox state={state.isChecked} onChange={e =>props.onChangeHandler(e)}>
                    Yes! I want emails!
                </Checkbox>
            </div>
        </div>
    );
};
export default IsInSlideShow;
