import React,{useEffect,useState,useContext} from 'react';
import Switch from "react-switch";
import './IsInSlideShow.scss';
import { AppContext } from "../../../../../context/AppContext";


const IsInSlideShow = props => {
    const contextData = useContext(AppContext);
    const [state, setState] = useState({
        isChecked : false
    });

    // useEffect(() => {
    //    contextData.dispatchEditingPostData(postData=>({
    //        ...postData,
    //        inSlideShow:state.isChecked
    //    }))
    // }, [state.isChecked]);

    useEffect(()=>{
        const isChecked = !!contextData.editingPostData.inSlideShow;
        setState({
            ...state,
            isChecked
        })
    },[ ]);

    const onChangeHandler = (e)=>{
        contextData.dispatchEditingPostData(postData=>({
            ...postData,
            inSlideShow:e
        }));
    };

    return (
        <div className='IsInSlideShow VideoInformationSection'>
            <div className="title">
                <p>Slide Show</p>
            </div>
            <div className="editor ">
                <Switch onChange={e=>onChangeHandler(e)} checked={!!contextData.editingPostData.inSlideShow} />
            </div>
        </div>
    );
};
export default IsInSlideShow;
