import React, { useEffect, useState, useContext, useRef } from 'react';

const AddWidgetWithPositionMenu = props => {
    const [ state, setState ] = useState({
        open:false
    });
    useEffect(() => {
    }, []);

    const onOpenHandler =()=>{
        state.open?setState({
            ...state,
            open: false
        }):setState({
            ...state,
            open: true
        })
    }

    if (state.open){
        return (
            <div className='AddWidgetWithPositionMenu'>
                <button onClick={()=>onOpenHandler()}>{props.type}</button>
                <div className="AddWidgetWithPositionMenuPositions">
                    <button>Home Page</button>
                    <button>Home Page Sidebar</button>
                    <button>Post Page Sidebar</button>
                    <button>Posts Page Sidebar</button>
                    <button>Tags Page Sidebar</button>
                    <button>Categories Page Sidebar</button>
                    <button>Actors Page Sidebar</button>
                    <button>Footer</button>
                </div>
            </div>
        );
    }else {
        return (
            <div className='AddWidgetWithPositionMenu'>
                <button>{props.type}</button>
            </div>
        );
    }


};
export default AddWidgetWithPositionMenu;
