    const renderComments = props.data.map(comment=>{
        return(
            <div className='recent-comments-item'>
                <p>{comment.author}</p>
            </div>
        )
    })
    return (
        <div className='recent-comments'>
            {renderComments}
        </div>
    );
};
export default RecentComments;
