import React, { useEffect, useState, useContext } from 'react';
import './TopBar.scss';
import { AppContext } from "../../../context/AppContext";
import FA from "react-fontawesome";

const TopBar = props => {
    const contextData = useContext(AppContext);

    const AdminSideBarOpenCloseHandler=()=>{
        contextData.settings.adminPanelSideBar ?
            contextData.dispatchSettings(settings=>({
                ...settings,
                adminPanelSideBar:false,
            })):
            contextData.dispatchSettings(settings=>({
                ...settings,
                adminPanelSideBar:true,
            }))
    };


    useEffect(()=>{
        console.log(contextData.settings )
    },[ contextData.settings]);

    return (
        <div className='adminTopBar'>
            <button className='navigationMobileBtn' onClick={()=>AdminSideBarOpenCloseHandler()}> xxx<FA name="bars" /> </button>
        </div>
    );
};
export default TopBar;
