import React, { useEffect, useState, useContext, useRef } from 'react';
import { AppContext } from '../../../context/AppContext';
import withRouter from 'next/dist/client/with-router'
import './FixedVideoPlayer.scss'

const FixedVideoPlayer = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({});
    useEffect(() => {
        console.log(contextData.fixedVideoPlayerData)
        setState(contextData.fixedVideoPlayerData)
        window.playerData = contextData.fixedVideoPlayerData.videoEmbedCode
    }, [ contextData.fixedVideoPlayerData ]);
    useEffect(() => {
        console.log(window.playerData )
    }, [props]);
    if (contextData.fixedVideoPlayerData.active) {
        return (
            <div className='fixed-video-player'>
                <p>{ contextData.fixedVideoPlayerData.title }</p>
                <div className='fixed-video-player-content'>
                    <iframe src={ state.videoEmbedCode } frameBorder="0" width='640' height='360' scrolling="no"/>
                </div>
            </div>
        );
    } else return null

};
export default withRouter(FixedVideoPlayer);
