import React, { useEffect, useState, useContext } from 'react';
import './Videos.scss';
import { AppContext } from "../../../context/AppContext";
import VideoElement from "../VideoElement/VideoElement";
import Pagination from "../Pagination/Pagination";
import withRouter from "next/dist/client/with-router";

const Videos = props => {
    const contextData = useContext(AppContext);
    const [ postsData, setPostsData ] = useState({
        pageNo: 1,
        size: 12,
        totalPosts: 0,
        postType: 'all',
        keyword: '',
        status: 'all',
        author: 'all',
        fields: [ 'title', 'mainThumbnail', 'quality', 'likes', 'disLikes', 'views', 'duration' ],
        checkedPosts: [],
    });
    const [ posts, setPosts ] = useState([]);
    useEffect(() => {
        contextData.functions.getPosts(postsData).then(res => {

            setPosts(res.data.posts)
            setPostsData({
                ...postsData,
                totalPosts: res.data.totalCount
            })
        })
    }, []);

    useEffect(() => {
        console.log(props)
    }, [ props ]);
    const renderPosts = posts.map(post => {
        return (
            <VideoElement key={ post._id } state={ post }/>
        )
    })

    return (
        <div className='Videos'>
            <div className='videoContent'>
                { renderPosts }
            </div>
            <Pagination { ...props } postsData={ postsData } setPostsData={ setPostsData }/>
        </div>
    );
};

Videos.getInitialProps =  ({ query }) => {
    const contextData = useContext(AppContext);
    let test= 1233
    console.log(123 )
    return { user:{
        name:'nima',
            lastname:'xxx'
        } }
};
export default withRouter(Videos);
