import React,{useEffect,useState,useContext} from 'react';
import {AppContext} from "../../../../../../context/AppContext";
const FilterStatusBtn = props => {
    let contextData = useContext(AppContext);
    const [state, setState] = useState({
        buttons:['All','Published','Draft','Trash']
    });
    useEffect(()=>{
    },[]);

    const onClickHandler = e=>{
        contextData.setPostsData({
            ...contextData.postsData,
            status:e.target.name,
            pageNo:1,
            author:'All'
        })
    };

    let clickOnMineHandler = ()=>{
        contextData.setPostsData({
            ...contextData.postsData,
            author:contextData.adminData._id
        })
    };

    const renderBtns = state.buttons.map(btn=>{
        let btnClassName ;
        if (contextData.postsData.status === btn){
            btnClassName = 'active'
        }else {
            btnClassName = 'inactive'
        }
        return(
            <button key={btn} className={btnClassName} name={`${btn}`} onClick={e=>onClickHandler(e)}>{btn}</button>
        )
    })

    return (
        <div className='FilterStatusBtn'>
            {renderBtns}
        </div>
    );
};
export default FilterStatusBtn;
