import React, { useEffect, useState, useContext } from 'react';
import { AppContext } from "../../../../../context/AppContext";

const Duration = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        h: 0,
        m: 0,
        s: 0
    });

   const onChangeHandler = e =>{
       setState({
           ...state,
           [e.target.name]:e.target.value
       })
   };


   const onBlurHandler = ()=>{
           let time = (state.h *3600) + (state.m *60) + state.s;
            contextData.dispatchEditingPostData(prevData=>({
                ...prevData,
                duration:time
            }))
   };


    return (
        <div className='Duration VideoInformationSection'>
            <div className="title">
                <p>Duration</p>
            </div>
            <div className="editor">
                <div className="durationItems">
                    <div className="durationItem">
                        <input name='durationH' type="number"
                               min="0" max="10"  onChange={e=>props.onChangeHandler(e)}/>
                        <label>H</label>
                    </div>
                    <div className="durationItem">
                        <input  name='durationM' type="number"
                               min="0" max="60"  onChange={e=>props.onChangeHandler(e)}/>
                        <label>M</label>
                    </div>
                    <div className="durationItem">
                        <input  name='durationS' type="number"
                               min="0" max="60"  onChange={e=>props.onChangeHandler(e)}/>
                        <label>S</label>
                    </div>
                </div>
            </div>
        </div>
    );
};
export default Duration;
