import React,{useEffect,useState,useContext} from 'react';
import Switch from "react-switch";
import './IsInSlideShow.scss';
import { AppContext } from "../../../../../context/AppContext";


const IsInSlideShow = props => {
    const contextData = useContext(AppContext);
    const [state, setState] = useState({
        isChecked : contextData.editingPostData.inSlideShow
    });
    useEffect(() => {
        console.log(props)
    }, []);

    
    const onChangeHandler = (e)=>{
        console.log(e )
    };

    return (
        <div className='IsInSlideShow VideoInformationSection'>
            <div className="title">
                <p>Slide Show</p>
            </div>
            <div className="editor ">
                <Switch onChange={e=>onChangeHandler(e)} checked={state.isChecked} />
            </div>
        </div>
    );
};
export default IsInSlideShow;
