import React,{useEffect,useState,useContext,useRef} from 'react';
import {AppContext} from "../../../../../../context/AppContext";
const FilterPagination = props => {
    let contextData = useContext(AppContext);
    let currentPageInput = useRef(null)
    let size = useRef(null)
    const [state, setState] = useState({
        totalPosts: 0,
        perPage: 0,
        totalPage: 0,
        currentPage: 1
    });
    useEffect(()=>{
        currentPageInput.current.value = contextData.postsData.pageNo
        size.current.value = contextData.postsData.size
    },[contextData.postsData.pageNo]);

    let nextPage = () =>{
        contextData.setPostsData({
            ...contextData.postsData,
            pageNo:contextData.postsData.pageNo +1
        })
    };
    let previousPage = () =>{
        if ((contextData.postsData.pageNo -1 ) <= 0){
            contextData.setPostsData({
                ...contextData.postsData,
                pageNo:1
            })
        }else {
            contextData.setPostsData({
                ...contextData.postsData,
                pageNo:contextData.postsData.pageNo -1
            })
        }

    };
    let lastPage = () =>{
        contextData.setPostsData({
            ...contextData.postsData,
            pageNo:Math.floor(contextData.postsData.totalCount/contextData.postsData.size)
        })
    };

    let firstPage = () =>{
        contextData.setPostsData({
            ...contextData.postsData,
            pageNo: 1
        })
    };

    let changePageNoManually = ()=>{
        if (currentPageInput.current.value <= 0){
            contextData.setPostsData({
                ...contextData.postsData,
                pageNo: 1
            })
            currentPageInput.current.value = 1
        }else {
            contextData.setPostsData({
                ...contextData.postsData,
                pageNo: currentPageInput.current.value
            })
        }
    };

    let changeSizeHandler =()=>{
        contextData.setPostsData({
            ...contextData.postsData,
            size: parseInt(size.current.value)
        })
    }


    return (
        <div className='FilterPagination'>
            <label className='totalCount'>{contextData.postsData.totalCount}</label>
            <button onClick={()=>firstPage()} className='actionBtn'><i className="fas fa-angle-double-left"/></button>
            <button onClick={()=>previousPage()} className='actionBtn'><i className="fas fa-angle-left"/></button>
            <input className='pageNumberInput' ref={currentPageInput} type='number' min={0} />
            <button  onClick={()=>changePageNoManually()}>Go</button>
            <label>of {Math.floor(contextData.postsData.totalCount/contextData.postsData.size)}</label>
            <button onClick={()=>nextPage()} className='actionBtn'><i className="fas fa-angle-right"/></button>
            <button onClick={()=>lastPage()} className='actionBtn'><i className="fas fa-angle-double-right"/></button>
            <select ref={size} defaultValue={contextData.postsData.size} onChange={()=>changeSizeHandler()} >
                <option value={5}>5</option>
                <option value={10}>10</option>
                <option value={20}>20</option>
                <option value={50}>50</option>
                <option value={100}>100</option>
                <option value={200}>200</option>
                <option value={500}>500</option>
            </select>
            {/*<input ref={size} onChange={()=>changeSizeHandler()} />*/}
        </div>
    );
};
export default FilterPagination;
