import React, { useEffect, useState, useContext } from 'react';
import { AppContext } from "../../../../context/AppContext";

const Format = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        postType: 'standard'
    });
    useEffect(() => {
       console.log(  contextData.editingPostData)
        if (contextData.editingPostData.postType){
            setState({
              postType:contextData.editingPostData.postType
            })
        }else {
            setState({
                postType:'standard'
            })
        }
    }, [contextData.editingPostData.postType]);

    return (
        <div className='Format'>
            <select value={ state.postType } name='format' onChange={ e => props.onChangeHandler(e) }>
                <option value='standard'>Standard</option>
                <option value='video'>Video</option>
            </select>
        </div>
    );
};

Format.getInitialProps = ({ req }) => {
    return {}
};

export default Format;
