import React, { useEffect, useState, useContext, useRef } from 'react';
import './AdminRenderComments.scss'

const AdminRenderComments = props => {

    const [hovered,setHovered] = useState('')
    const [ checkedItems, setCheckedItems ] = useState([])

    useEffect(() => {
        console.log(checkedItems)
    }, [ checkedItems ]);

    const onChangeHandler = (e, id) => {
        e.target.checked ?
            setCheckedItems([ ...checkedItems, id ]) :
            setCheckedItems(checkedItems.filter(i => i !== id))
    }


const commentStatusChanger = ()=>{

}




    const renderComments = props.comments.map(comment => {
        const HoveredHandler = ()=>{
            if (hovered === comment._id){
                return(
                    <div className='commentControl'>
                        <button>Approve</button>
                        <button>Delete</button>
                        <button>Spam</button>
                        <button>Pending</button>
                    </div>
                )
            }else return null

        }
        return (
            <div key={ props.comments.indexOf(comment) } className='adminCommentsItem'  onMouseOver={()=>setHovered(comment._id)}>
                <div className="adminCommentsItemHead">
                    <input type='checkbox' checked={ checkedItems.includes(comment._id) } onChange={e=>onChangeHandler(e,comment._id)}/>
                    <p>{comment.author}</p>
                </div>
                <div className="adminCommentsItemBody">
                    <p>{ comment.body }</p>
                    <HoveredHandler/>
                </div>
            </div>
        )
    })
    return (
        <div className='AdminRenderComments'>
            { renderComments }
        </div>
    );
};
export default AdminRenderComments;
