import React, { useEffect, useState, useContext, useRef } from 'react';
import { AppContext } from '../../../../context/AppContext'

const VideoWidgetModel = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        title: props.data.title || '',
        category: props.data.category || 'all',
        tags: props.data.tags || 'all',
        count: props.data.count || 6,
        pagination: props.data.pagination || false,
        redirectLink: props.data.redirectLink || '',
        type: props.data.type || 'videoBlocks',
        index: props.data.index || 0
    });
    useEffect(() => {
        console.log(contextData.widgetsSettings.homeWidgets.indexOf(props.data))
    }, []);

    const onDeleteHandler = () => {
        contextData.dispatchWidgetsSettings({
            homeWidgets: contextData.widgetsSettings.homeWidgets.filter(w => w.index !== state.index)
        })
    }

    return (
        <div className='VideoWidgetModel'>
            <input className='title' placeholder='widgetTitle' value={ state.title }/>
            <input className='category' placeholder='widgetCategories' value={ state.category }/>
            <input className='tags' placeholder='widgetTags' value={ state.tags }/>
            <input className='redirectLink' placeholder='pathToRedirect' value={ state.redirectLink }/>
            <input type='number' className='count' placeholder='count' value={ state.count }/>
            <span>Pagination:</span>
            <select name='pagination' value={ state.pagination }>
                <option value={ false }>false</option>
                <option value={ true }>true</option>
            </select>
            <div className='control'>
                <button>save</button>
                <button onClick={()=>onDeleteHandler()}>delete</button>
            </div>

        </div>
    );
};
export default VideoWidgetModel;
