import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout'
import {getComments} from '../../../_variables/ajaxPostsVariables'

const comments = props => {
    const [ state, setState ] = useState({});
    useEffect(() => {
        console.log( props)
    }, [props]);
    return (
        <AdminLayout>
            <div>
                comments
            </div>
        </AdminLayout>
    );
};

comments.getInitialProps = async ({ pathname, query, req, res, err }) =>{
    let comments;
    const getCommentsData = {
        size: parseInt(query.size) || parseInt(identity.postsCountPerPage) || 30,
        pageNo: parseInt(query.page) || 1,
        keyword: query.keyword || '',
        sort: query.sort || 'latest',
        // status: 'published',
    }
    const commentsData = await getComments(getCommentsData)
    comments=commentsData.data.comments
    return {  query,pathname,comments }
}
export default comments;
