import React, { useEffect, useState, useContext, useRef } from 'react';

const AdminCommentItem = props => {
    const [ state, setState ] = useState({
        hovered: false,
        checked: false
    });

    const [ commentData, setCommentData ] = useState({})

    useEffect(() => {
        setCommentData(props.data)
    }, [ props.data ]);

    useEffect(() => {
        console.log( commentData)
    }, [ commentData ]);
    const updateComment = () => {

    }

    const HoveredHandler = () => {
        if (state.hovered) {
            return (
                <div className='commentControl'>
                    <button>Approve</button>
                    <button>Delete</button>
                    <button>Spam</button>
                    <button>Pending</button>
                </div>
            )
        } else return null

    }

    return (
        <div key={ props.data._id } className='adminCommentsItem' onMouseOver={ () => setState({ ...state, hovered: true }) } onMouseLeave={ () => setState({ ...state, hovered: false }) }>
            <div className="adminCommentsItemHead">
                <input type='checkbox' checked={ state.checked } onChange={ () => state.checked ? setState({ ...state, checked: false }) : setState({ ...state, checked: true }) }/>
                <p>{ props.data.author }</p>
            </div>
            <div className="adminCommentsItemBody">
                <p>{ props.data.body }</p>
                <HoveredHandler/>
            </div>
        </div>
    );
};
export default AdminCommentItem;
