import React, { useContext, useEffect } from 'react';
import ReactDOM from 'react-dom'
import Head from "next/head";
import Header from "../includes/Header/Header";
import '../../styles/styles.scss';
import TopBar from "../includes/TopBar/TopBar";
import Navigation from "../includes/Header/Navigation/Navigation";
import Loading from "../includes/Loading/Loading";
import FixedVideoPlayer from '../includes/FixedVideoPlayer/FixedVideoPlayer'

const AppLayout = props => {

    useEffect(() => {
        const fixedVideoPlayerElement = document.querySelector('#fixed-video-player')
        if(!fixedVideoPlayerElement){
            const fixedVideoPlayerElementDiv = document.createElement('div')
            fixedVideoPlayerElementDiv.id='fixed-video-player'
            document.querySelector('body').appendChild(fixedVideoPlayerElementDiv)
        }
        console.log(fixedVideoPlayerElement )
    }, []);

    return (
        <>
            <TopBar/>
            <Header/>
            <Navigation/>
            <Loading/>
            <FixedVideoPlayer/>
            <div className="App">
                { props.children }
            </div>
        </>
    );
};

export default AppLayout;
