import React, { useEffect, useState, useContext } from 'react';

const VideoPlayer = props => {
    // useEffect(() => {
    //     console.log(props)
    // }, [ props ]);
//"video/mp4"
    const WhatToRender = () => {

        if ( props.videoUrl) {
            return (
                <>
                    <video>
                        <source src={ props.videoUrl }/>
                    </video>
                </>
            )
        } else if (!props.videoUrl && props.iframeCode) {
            return (
                <iframe src={ props.iframeCode } frameBorder="0" width='640' height='360' scrolling="no"/>
            )
        } else return null
    }

    if (props.iframeCode) {
        return (
            <div className='video-player'>
                <meta itemProp="name" content={ props.title }/>
                <meta itemProp="description" content={ props.description }/>
                <meta itemProp="duration" content={ props.duration }/>
                <meta itemProp="thumbnailUrl" content={ props.thumbnailUrl }/>
                <meta itemProp="embedURL" content={ props.embedURL }/>
                <meta itemProp="uploadDate" content={ props.uploadDate }/>
                <div className="responsive-player">
                    <WhatToRender/>
                    {/*<iframe src={ props.videoUrl } frameBorder="0" width='640' height='360' scrolling="no"/>*/}
                </div>

            </div>

        );
    } else return null

};
export default VideoPlayer;
