            <TagsAndCategoriesActors type='actors' data={props.actors} />
            <TagsAndCategoriesActors type='tags' data={props.tags} />
            <TagsAndCategoriesActors type='categories' data={props.categories} />
        </div>
    );
};
export default PostInfo;
