import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from "../../../components/layouts/AdminLayout";
import settings from '../settings/general'
import { getSetting, updateSetting } from '../../../_variables/ajaxVariables'
import './design.scss'
import { AppContext } from '../../../context/AppContext'

const design = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        topBarBackgroundColor: props.design.topBarBackgroundColor || '#181818',
        topBarTextColor: props.design.topBarTextColor || '#fff',
        headerBackgroundColor: props.design.headerBackgroundColor || '#000',
        headerTextColor: props.design.headerTextColor || '#fff',
        navigationBackgroundColor: props.design.navigationBackgroundColor || '#181818',
        navigationTextColor: props.design.navigationTextColor || '#fff',
        bodyBackgroundColor: props.design.bodyBackgroundColor || '#000',
        bodyTextColor: props.design.bodyTextColor || '#000',
        footerBackgroundColor: props.design.footerBackgroundColor || '#181818',
        footerTextColor: props.design.footerTextColor || '#fff',
        widgetHeaderBackgroundColor: props.design.widgetHeaderBackgroundColor || '#222222',
        widgetHeaderTextColor: props.design.widgetHeaderTextColor || '#fff',
    });

    const onChangeHandler = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }

    const onSubmitHandler = e => {
        e.preventDefault()
        contextData.dispatchState({
            ...contextData.state,
            loading:true
        })
        updateSetting('design', state).then(()=>{
            contextData.dispatchState({
                ...contextData.state,
                loading:false
            })
        })
    };

    const renderDesignFields = Object.keys(state).map(element=>{
        return(
            <div className="adminDesignSection">
                <div className="adminDesignSectionItems">
                    <div className="adminDesignSectionItem">
                        <p>{element.match(/[A-Z][a-z]+/g)} :</p>
                        <input name={element} value={ state[element] } onChange={ e => onChangeHandler(e) }/>
                        <div className="previewColor" style={ { backgroundColor: state[element] } }/>
                    </div>
                </div>
            </div>
        )
    })




    return (
        <AdminLayout>
            <form className='adminDesign' onSubmit={ e => onSubmitHandler(e) }>
                {renderDesignFields}
                {/*<div className="adminDesignSection">*/}
                {/*    <h2>Top Bar:</h2>*/}
                {/*    <div className="adminDesignSectionItems">*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Top Bar Background Color :</p>*/}
                {/*            <input name='topBarBackgroundColor' value={ state.topBarBackgroundColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.topBarBackgroundColor } }/>*/}
                {/*        </div>*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Top Bar Text Color :</p>*/}
                {/*            <input name='topBarTextColor' value={ state.topBarTextColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.topBarTextColor } }/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
                {/*<div className="adminDesignSection">*/}
                {/*    <h2>Header:</h2>*/}
                {/*    <div className="adminDesignSectionItems">*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Header Background Color :</p>*/}
                {/*            <input name='headerBackgroundColor' value={ state.headerBackgroundColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.headerBackgroundColor } }/>*/}
                {/*        </div>*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Header Text Color :</p>*/}
                {/*            <input name='headerTextColor' value={ state.headerTextColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.headerTextColor } }/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
                {/*<div className="adminDesignSection">*/}
                {/*    <h2>Navigation Menu:</h2>*/}
                {/*    <div className="adminDesignSectionItems">*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Header Background Color :</p>*/}
                {/*            <input name='navigationBackgroundColor' value={ state.navigationBackgroundColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.navigationBackgroundColor } }/>*/}
                {/*        </div>*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Header Text Color :</p>*/}
                {/*            <input name='navigationTextColor' value={ state.navigationTextColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.navigationTextColor } }/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
                {/*<div className="adminDesignSection">*/}
                {/*    <h2>Body :</h2>*/}
                {/*    <div className="adminDesignSectionItems">*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Body Background Color :</p>*/}
                {/*            <input name='bodyBackgroundColor' value={ state.bodyBackgroundColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.bodyBackgroundColor } }/>*/}
                {/*        </div>*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Body text Color :</p>*/}
                {/*            <input name='bodyTextColor' value={ state.bodyTextColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.bodyTextColor } }/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
                {/*<div className="adminDesignSection">*/}
                {/*    <h2>Footer:</h2>*/}
                {/*    <div className="adminDesignSectionItems">*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Footer Background Color :</p>*/}
                {/*            <input name='footerBackgroundColor' value={ state.footerBackgroundColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.footerBackgroundColor } }/>*/}
                {/*        </div>*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Footer Text Color :</p>*/}
                {/*            <input name='footerTextColor' value={ state.footerTextColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.footerTextColor } }/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
                {/*<div className="adminDesignSection">*/}
                {/*    <h2>Widget Header:</h2>*/}
                {/*    <div className="adminDesignSectionItems">*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Widget Header Background Color :</p>*/}
                {/*            <input name='widgetHeaderBackgroundColor' value={ state.widgetHeaderBackgroundColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.widgetHeaderBackgroundColor } }/>*/}
                {/*        </div>*/}
                {/*        <div className="adminDesignSectionItem">*/}
                {/*            <p>Widget Header Text Color :</p>*/}
                {/*            <input name='widgetHeaderTextColor' value={ state.widgetHeaderTextColor } onChange={ e => onChangeHandler(e) }/>*/}
                {/*            <div className="previewColor" style={ { backgroundColor: state.widgetHeaderTextColor } }/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
                <button className='submitBtn' type='submit'>save settings</button>
            </form>
        </AdminLayout>
    );
};

design.getInitialProps = async ({ pathname, query, req, res, err }) => {
    let design;
    const designData = await getSetting('design');
    design = designData.data.setting ? designData.data.setting.data : {}
    return { design }
}
export default design;
