import React, { useEffect } from 'react';
import { AppProviderWithRouter, AppContext } from "../context/AppContext";

const MyApp = ({ Component, pageProps },props) => {

    useEffect(()=>{
        console.log( pageProps)
    },[pageProps ]);
    return (
        <AppProviderWithRouter>
            <Component { ...pageProps } />
        </AppProviderWithRouter>
    )

};
