import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout'
import FileManagerControl from '../../../components/adminIncludes/FileManagerComponents/FileManagerControl/FileManagerControl'
import FileManagerArea from '../../../components/adminIncludes/FileManagerComponents/FileManagerArea/FileManagerArea';
import { readPath } from '../../../_variables/_ajaxFilesVariables'
import { AppContext } from '../../../context/AppContext'

const fileManager = props => {
    const contextData = useContext(AppContext);
    const [ state, setState ] = useState({
        path: '.',
        files: [],
        clickedItem: '',
        file: '',
        editFile: false,
        action: '',
        _do: '',
        // AlertBox:false,
        DeleteAlertBox: false,
        confirm: Date.now(),
        didDataChange: false,
        message: '',
        report: '',
        inputBox: false,
        newItemName: ''
    });

    // useEffect(() => {
    //     console.log(state)
    // }, [ state ]);

    useEffect(() => {
        setData()
    }, [state.path,state.didDataChange]);

    const setData = () => {
        contextData.dispatchState({
            ...contextData.state,
            loading: true
        });
        readPath(state.path).then(res => {
            if (res.data.type === 'dir') {
                setState({
                    ...state,
                    files: res.data.data
                })
                contextData.dispatchState({
                    ...contextData.state,
                    loading: false
                });
            } else if (res.data.type === 'file') {
                setState({
                    ...state,
                    file: res.data.data
                });
                contextData.dispatchState({
                    ...contextData.state,
                    loading: false
                });
            } else if (res.data.type === undefined) {
                setState({
                    ...state,
                    error: true
                })
                contextData.dispatchState({
                    ...contextData.state,
                    loading: false
                });
            } else {
                setState({
                    ...state,
                    error: true
                })
                contextData.dispatchState({
                    ...contextData.state,
                    loading: false
                });
            }
        }).catch(err => {
            contextData.dispatchState({
                ...contextData.state,
                loading: false
            });
        })
    }
    const setStateHandler = (key, value) => {
        setState({
            ...state,
            key: value
        })
    }

    return (
        <AdminLayout>
            <div className='fileManager'>
                <FileManagerControl setStateHandler={ setStateHandler } data={state}/>
                <FileManagerArea setStateHandler={ setStateHandler }  data={state}/>
            </div>
        </AdminLayout>
    );
};
export default fileManager;
