import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../../components/layouts/AdminLayout';
import { DelayInput } from 'react-delay-input';
import './customScript.scss';
import { AppContext } from '../../../../context/AppContext'
import { getAbsolutePath } from '../../../../_variables/_variables'
import { getSetting } from '../../../../_variables/ajaxVariables'
import settings from '../general'

const customScript = props => {
    const contextData = useContext(AppContext);

    const [ state, setState ] = useState({
        customScripts:[]
    })

    const [ newScript, setNewScript ] = useState({
        scriptName: '',
        scriptBody: ''
    })

    useEffect(() => {
        setState({
            customScripts: contextData.siteIdentity.customScripts || []
        })
    }, [ contextData.siteIdentity.customScripts ]);

    const onChangeHandler = e => {
        contextData.dispatchSiteIdentity({
            ...contextData.siteIdentity,
            customScript: e.target.value
        })
    }

    const onSaveHandler = () => {
        contextData.functions.updateSetting('identity', contextData.siteIdentity).then(() => {
            contextData.dispatchState({
                ...contextData.state,
                loading: false
            })
        })
    }
    // useEffect(() => {
    //     console.log(contextData.siteIdentity )
    // }, [contextData.siteIdentity]);

    const onNewScriptChangeHandler = e => {
        setNewScript({
            ...newScript,
            [e.target.name]: e.target.value
        })
    }

    const onAddHandler = e => {
        e.preventDefault()
        const newScriptData = [ ...contextData.customScripts || [], newScript ]
        contextData.dispatchSiteIdentity({
            ...contextData.siteIdentity,
            customScripts: newScriptData
        })
    }
//[ ...contextData.customScripts, newScript ]
    const renderScripts = state.customScripts.map(script => {
        return (
            <div className='customScriptPageItem'>
                <p>{ script.scriptName }</p>
                <DelayInput element="textarea" className='customScript' name='videoEmbedCode' value={ script } delayTimeout={ 1000 } onChange={ e => onChangeHandler(e) }/>
                <button onClick={ () => onSaveHandler() }>Save</button>
            </div>
        )
    })

    return (
        <AdminLayout>

            <form className='addCustomScriptForm' onSubmit={e => onAddHandler(e)}>
                <input name='scriptName' placeholder='Script Name' onChange={ e => onNewScriptChangeHandler(e) }/>
                <textarea name='scriptBody' className='addScriptTextarea' placeholder='Script with out Script Tag' onChange={ e => onNewScriptChangeHandler(e) }/>
                <button type='submit' >Add</button>
            </form>
            <div className='customScripts'>
                {renderScripts}
            </div>
            {/*<div className='customScriptPage'>*/}
            {/*    <DelayInput element="textarea" className='customScript' name='videoEmbedCode' value={ contextData.siteIdentity.customScript } delayTimeout={ 1000 } onChange={ e => onChangeHandler(e) }/>*/}
            {/*    <button onClick={ () => onSaveHandler() }>Save</button>*/}
            {/*</div>*/}
        </AdminLayout>
    );
};

export default customScript;
