import React, { useEffect, useState, useContext, useRef } from 'react';

const AdminRenderComments = props => {
    const [ state, setState ] = useState({
        checkedItems: []
    });

    const [ checkedItems, setCheckedItems ] = useState([])
    useEffect(() => {
        console.log(checkedItems)
    }, [ checkedItems ]);

    const onChangeHandler = (e, id) => {
        e.target.checked ?
            setCheckedItems([ ...checkedItems, id ]) :
            setCheckedItems(checkedItems.filter(i => i !== id))
    }

    const renderComments = props.comments.map(comment => {
        return (
            <div key={ props.comments.indexOf(comment) } className='adminCommentsItem'>
                <input type='checkbox' checked={ checkedItems.includes(comment._id) } onChange={e=>onChangeHandler(e,comment._id)}/>
                <p>{ comment.body }</p>
            </div>
        )
    })
    return (
        <div className='AdminRenderComments'>
            { renderComments }
        </div>
    );
};
export default AdminRenderComments;
