import React, {Component, useEffect, useState, useContext } from 'react';
import './Videos.scss';
import { AppContext } from "../../../context/AppContext";
import VideoElement from "../VideoElement/VideoElement";
import Pagination from "../Pagination/Pagination";
import withRouter from "next/dist/client/with-router";
import Home from "../../../pages";

const Videos = props => {
    const contextData = useContext(AppContext);
    const [ postsData, setPostsData ] = useState({
        pageNo: 1,
        size: 12,
        totalPosts: 0,
        postType: 'all',
        keyword: '',
        status: 'all',
        author: 'all',
        fields: [ 'title', 'mainThumbnail', 'quality', 'likes', 'disLikes', 'views', 'duration' ],
        checkedPosts: [],
    });
    const [ posts, setPosts ] = useState([]);
    useEffect(() => {
        console.log( props)
    }, []);
    const renderPosts = posts.map(post => {
        return (
            <VideoElement key={ post._id } state={ post }/>
        )
    })
    return (
        <div className='Videos'>
            <div className='videoContent'>
                { renderPosts }
            </div>
            <Pagination { ...props } postsData={ postsData } setPostsData={ setPostsData }/>
        </div>
    );

};
Videos.getInitialProps = async (ctx)=>{
let testData = {
    name:'gholi'
}
    return {testData}
};

export default withRouter(Videos);1
