import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout';
import {executor} from '../../../_variables/ajaxVariables';
import { animateScroll,Scroll   } from "react-scroll";
import './tools.scss'

const tools = props => {
    const logElement = useRef(null)
    const [ state, setState ] = useState({
        command: 'dir',
        log:''
    });
    useEffect(() => {
    }, []);

    const onchangeHandler = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }

    const onExecutorHandler = (e,command)=>{
        e.preventDefault()
        executor(command).then(res=>{
            setState({
                ...state,
                log: state.log  + res.data.response
            })
            console.log( res.data)
        })
        animateScroll.scrollToBottom({containerId: logElement})
    }


    return (
        <AdminLayout>
            <div className='adminTools'>
               <h2> Terminal :</h2>
                <div className='quiqAccess'>
                    <h3>Quick Access</h3>
                    <button onClick={()=>onExecutorHandler(null,state.command)}>Build</button>
                </div>
                <textarea ref={logElement} className='terminalLog' value={state.log} onChange={e=>e.scrollTop = e.offsetHeight} />
                <form className="terminal" onSubmit={e=>onExecutorHandler(e,state.command)}>
                    <input name='command' type="text" onChange={ e => onchangeHandler(e) }/>
                    <button type='submit'>EXE</button>
                </form>
            </div>
        </AdminLayout>
    );
};
export default tools;
