import React, { useEffect, useState, useContext } from 'react';
import './DropDownWidget.scss';
import FA from "react-fontawesome";

const DropDownWidget = (Component,title) => {
    const [ state, setState ] = useState({
        open: true,
        icon: 'sort-up'
    });

    useEffect(() => {
        state.open?
            setState({
                ...state,
                icon:'sort-up'
            }):
            setState({
                ...state,
                icon:'sort-down'
            })
    }, [ state.open ]);

    const openCloseHandler = ()=>{
        state.open?
            setState({
                ...state,
                open:false
            }):
            setState({
                ...state,
                open:true
            })
    };

    return (
        <div className='DropDownWidget'>
            <div className="DropDownWidgetHead">
                <p className='DropDownWidgetHeadTitle'>Title{ title }</p>
                <button className='DropDownWidgetHeadOpenCloseBtn' onClick={()=>openCloseHandler()}><FA className='fontawesomeMedium' name={ state.icon }/></button>
            </div>
            <Component/>
        </div>
    );
};
export default DropDownWidget;
