import React,{useEffect,useState,useContext} from 'react';
import './TitleDescription.scss'
import { AppContext } from "../../../../context/AppContext";
const TitleDescription = props => {
    const contextData = useContext(AppContext);
    const [state, setState] = useState({
    });

    return (
        <div className='TitleDescription'>
            <input name='title' className='TitleDescriptionTitle' placeholder='Enter The Title Here' onChange={e=>{
                contextData.functions.setEditingPostData(e.target.name,e.target.value)
            }}/>
            <textarea name='description' className='TitleDescriptionDescription' onChange={e=>props.onChangeHandler(e)}/>
        </div>
    );
};
export default TitleDescription;
