import React, { useEffect, useState, useContext, useRef } from 'react';

const HomePageSidebar = props => {
    const [ state, setState ] = useState({
        style:{
            gridArea:'sidebar'
        }
    });
    useEffect(() => {
        console.log( props)
    }, [props]);
    if (props.isActive){
        return (
            <div style={state.style} className='home-page-sidebar'>
                I am homepage sidebar
            </div>
        );
    }else return null

};
export default HomePageSidebar;
