import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout';
import {fileUpload} from '../../../_variables/ajaxVariables'

const gallery = props => {
    const uploadInputElement = useRef(null)
    const [ state, setState ] = useState({
          selectedFiles:[]
    });
    useEffect(() => {
    }, []);

    const onChangeHandler = async e => {

        const filesData = e.target.files.map(file=>{
            let selectedFileFormData = new FormData()
            selectedFileFormData.append(file.name, file);
            return selectedFileFormData
        })
        console.log(filesData )
         setState({
             selectedFiles: e.target.files
         })
        // selectedFileFormData.append('token',localStorage.wt)
    }

    const onUploadHandler = ()=>{
        let selectedFileFormData = new FormData()
        selectedFileFormData.append('selectedFileFormData', e.target.files[0]);
        fileUpload(selectedFileFormData,'test')
        console.log( selectedFileFormData)
    }



    return (
        <AdminLayout>
            <div className='gallery'>
                <div className='uploadControl'>
                    <input ref={ uploadInputElement } type="file" style={ { display: 'none' } } onChange={e=>onChangeHandler(e)}/>
                    <button onClick={ () => uploadInputElement.current.click() }>Upload</button>
                </div>

            </div>
        </AdminLayout>
    );
};
export default gallery;
