import React, { useEffect, useState, useContext, useRef } from 'react';
import AppLayout from '../components/layouts/AppLayout'
import { getMultipleSetting } from '../_variables/ajaxVariables'
import SiteSettingSetter from '../components/includes/SiteSettingsSetter/SiteSettingsSetter'
import { getAbsolutePath } from '../_variables/_variables'

const Error = props => {

    return (
        <AppLayout>
            <SiteSettingSetter { ...props }/>
            <p className='error-page-message'>
                { props.statusCode
                    ? `An error ${ props.statusCode } occurred on server`
                    : 'An error occurred on client' }
            </p>
        </AppLayout>
    )
}

Error.getInitialProps = async ({ req, res, err }) => {
    const statusCode = res ? res.statusCode : err ? err.statusCode : 404
    const data = res.settingData
    const domainName = req ? await getAbsolutePath(req) : ''
    let settings;
    const settingsData = await getMultipleSetting({ settings: [ 'identity', 'navigation', 'design' ] }, true, domainName)
    settings = settingsData.data.settings ? settingsData.data.settings : []

    return { statusCode, settings }
}

export default Error
