import React from 'react';
import './DropDownWidget.scss';
import FA from "react-fontawesome";

const DropDownWidget = ({Component}) => {
    let open = true;
    let icon = 'sort-up';
    const openCloseHandler = () => {
        if (open) {
            open = false;
            icon= 'sort-up'
        } else {
            open = true;
            icon= 'sort-down'
        }
    };
    return (
        <div className='DropDownWidget'>
            <div className="DropDownWidgetHead">
                <p className='DropDownWidgetHeadTitle'>Title</p>
                <button className='DropDownWidgetHeadOpenCloseBtn' onClick={ () => openCloseHandler() }><FA className='fontawesomeMedium' name={ icon }/></button>
            </div>
            <Component/>
        </div>
    );
};
export default DropDownWidget;
