import React, { useEffect, useState, useContext, useRef } from 'react';

const FileManagerArea = props => {
    const [ state, setState ] = useState({});
    useEffect(() => {
    }, []);

    useEffect(() => {
        console.log(props)
    }, [ props ]);




    const fileLogoDetector = fileName => {
        let nextClass = '';
        if (contextData.filesData.clickedItem === clickPathGenerator(fileName,contextData.filesData.path)) {
            nextClass = ' clickedItem'
        } else {
            nextClass = ' unClickedItem'
        }

        if (fileName.includes('.js')) {
            return "fab fa-js" + nextClass
        } else if (fileName.includes('.env')) {
            return "fas fa-sliders-h" + nextClass
        } else if (!fileName.includes('.')) {
            return "fas fa-folder" + nextClass
        } else if (fileName.includes('.scss')) {
            return "fab fa-sass" + nextClass
        } else return "fas fa-file" + nextClass
    };

    const onClickHandler = e => {
        let clickedItem = contextData.filesData.clickedItem;
        let filePath = clickPathGenerator(e.target.name,contextData.filesData.path);

        if (contextData.filesData.clickedItem === filePath ){
            contextData.setFilesData({
                ...contextData.filesData,
                clickedItem:''
            });
        }else{
            contextData.setFilesData({
                ...contextData.filesData,
                clickedItem:filePath
            });
        }
    };




    let renderDir = props.data.files.map(item => {
        return (
            <div key={item} className='dirItem'>
               <button
                   // ref={() => selectedItem.current[contextData.filesData.files.indexOf(item)]}
                   className={[fileLogoDetector(item)]} key={item} name={item} onClick={(e) => onClickHandler(e)}
                   onDoubleClick={(e) => onDoubleClickHandler(e)} onContextMenu={(e) => onContextMenuHandler(e)}/>

                <p> {item}</p>
            </div>
        )
    });
    return (
        <div className='FileManagerArea'>
            {renderDir}
        </div>
    );
};
export default FileManagerArea;

