import React, { useContext,useEffect } from 'react';
import Head from "next/head";
import Header from "../includes/Header/Header";
import '../../styles/styles.scss';
import TopBar from "../includes/TopBar/TopBar";
import Navigation from "../includes/Header/Navigation/Navigation";
import Loading from "../includes/Loading/Loading";
import settings from "../../pages/admin/settings";

const AppLayout = props => {


    useEffect(()=>{
        console.log(props )
    },[props ]);
    return (
        <>
            <Head>
                <title>Website Title</title>
                <meta name="theme-color" content="#000000"/>
                <meta name="viewport" content="width=device-width, initial-scale=1"/>
                <meta charSet="utf-8"/>
                <link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" rel="stylesheet"/>
                <meta name="description" content="description of the site"/>
                <meta name="keywords" content="key,word,for,SEO"/>
                <link rel="icon" href="/favicon.ico"/>
            </Head>
            <TopBar/>
            <Header/>
            <Navigation/>
            <Loading/>
            <div className="App">
                { props.children }
            </div>
        </>
    );
};

AppLayout.getInitialProps = async ({ pathname, query, req, res, err }) => {
    return { name: 'test' }
}
export default AppLayout;
