import React, { useEffect, useState, useContext, useRef } from 'react';
import AdminLayout from '../../../components/layouts/AdminLayout';
import {fileUpload} from '../../../_variables/ajaxVariables'
import request from 'superagent'

const gallery = props => {
    const uploadInputElement = useRef(null)
    const [ state, setState ] = useState({
          selectedFiles:[]
    });
    const [selectedFiles,setSelectedFiles]=useState(null)

    useEffect(() => {
    }, []);

    const onChangeHandler =  e => {

        const uploadRequest = request.post(window.location.origin + '/api/v1/settings/fileManagerControllers-uploadFile')
        uploadRequest.attach(e.target.files[0].name, e.target.files[0]);

        uploadRequest.end((err, res) => {
            if(err) {
                console.log('error response', err, res);
                alert(res.body.error);
            } else {
                alert(`File upload completed!`);
                // this.setState({'fileLink': `http://localhost:3000/api/files/${res.body.result}`});
            }
        });




        // const filesData = new FormData()
        // filesData.append('file',e.target.files[0])
        // fileUpload(filesData,'test')
        //
        // console.log(...filesData )
    }




    return (
        <AdminLayout>
            <div className='gallery'>
                <div className='uploadControl'>
                    <input ref={ uploadInputElement } type="file" style={ { display: 'none' } } onChange={e=>{

                        const uploadRequest = request.post(window.location.origin + '/api/v1/settings/fileManagerControllers-uploadFile')
                        uploadRequest.attach(e.target.files[0].name, e.target.files[0]);

                        uploadRequest.end((err, res) => {
                            if(err) {
                                console.log('error response', err, res);
                                alert(res.body.error);
                            } else {
                                alert(`File upload completed!`);
                                // this.setState({'fileLink': `http://localhost:3000/api/files/${res.body.result}`});
                            }
                        });

                        // const filesData = new FormData()
                        // filesData.append('file',e.target.files[0])
                        // fileUpload(filesData,'test')
                    }}/>
                    <button onClick={ () => uploadInputElement.current.click() }>Add</button>

                </div>
                {/*<form*/}
                {/*      // ref='uploadForm'*/}
                {/*      // id='uploadForm'*/}
                {/*      action={'/api/v1/settings/fileManagerControllers-uploadFile'}*/}
                {/*      method='post'*/}
                {/*      encType="multipart/form-data"*/}
                {/*      onSubmit={e=>e.preventDefault()}*/}
                {/*>*/}
                {/*     <input type="file" name="sampleFile" />*/}
                {/*     <input type='submit' value='Upload!' />*/}
                {/*</form>*/}

            </div>
        </AdminLayout>
    );
};
export default gallery;
